// Import necessary dependencies
import { useParams, useOutletContext } from 'react-router-dom';
import { useEffect } from 'react';

// UserProfile component
const UserProfile = () => {
    const setUser = useOutletContext()
    const { id } = useParams();

    useEffect(() => {
        fetch(`/users/${id}`)
        .then((resp) => {
            if (resp.ok) {
                resp.json().then((user) => setUser(user))
            } else {
                // handle what should happen if not logged in
                console.log('error')
            }
        })
    }, [])

    // Conditional rendering based on the presence of the id parameter
    return (
        <div>
        {id ? (
            // Render content for user with specific id
            <div>
            <h2>User Profile for ID: {id}</h2>
            {/* Add other profile-specific content here */}
            </div>
        ) : (
            // Render content for the current user (no specific id)
            <div>
            <h2>Current User Profile</h2>
            {/* Add content for the current user here */}
            </div>
        )}
        </div>
    );
};

export default UserProfile;
