// Import necessary dependencies
import { useParams, useOutletContext } from 'react-router-dom';
import { useEffect } from 'react';
import { OutletContext } from './App'
import { useContext } from 'react';

// UserProfile component
const UserProfile = () => {
    const { setUser }= useContext(OutletContext)
    const { id } = useParams();

    useEffect(() => {
        if(id){fetch(`/users/${id}`)
        .then((resp) => {
            if (resp.ok) {
                resp.json()
            } else {
                throw new Error('Not logged in')
            } 

        })
        .then((user) => {
            console.log(user)
            setUser(user)
        })
        .catch((err) => {
            console.log("Error getting user data:", err)
        })}
    }, [id, setUser])


    return (
        <div>
        {} ? (
            // Render content for user with specific id
            <div>
            <h2>{user ? `${user.name}'s Profile` : 'Loading...'}</h2>
            {/* Add other profile-specific content here */}
            </div>
        ) : (
            // Render content for the current user (no specific id)
            <div>
            <h2>Current User Profile</h2>
            {/* Add content for the current user here */}
            </div>
        )}
        </div>
    );
};

export default UserProfile;
