// Import necessary dependencies
import { useParams } from 'react-router-dom';
import { useEffect } from 'react';

// UserProfile component
const UserProfile = () => {
  // Access the id parameter from the URL
    const { id } = useParams();


    // Conditional rendering based on the presence of the id parameter
    return (
        <div>
        {id ? (
            // Render content for user with specific id
            <div>
            <h2>User Profile for ID: {id}</h2>
            {/* Add other profile-specific content here */}
            </div>
        ) : (
            // Render content for the current user (no specific id)
            <div>
            <h2>Current User Profile</h2>
            {/* Add content for the current user here */}
            </div>
        )}
        </div>
    );
};

export default UserProfile;
