import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import { Container, Row, Col } from 'react-bootstrap';
import { useState } from 'react';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import { useOutletContext,  } from 'react-router-dom'
import { NavLink } from 'react-router-dom';
import {Icon} from '../react-icons-kit';
import {eyeOff} from 'react-icons-kit/feather/eyeOff';
import {eye} from 'react-icons-kit/feather/eye'

// local imports
import styles from '../stylesheets/Signup.module.css'

function Signup() {
    const setUser = useOutletContext()
    const [type, setType] = useState('password');
    const [icon, setIcon] = useState(eyeOff);

    const handlePwViewToggle = () => {
        if (type==='password'){
            setIcon(eye);
            setType('text')
        } else {
            setIcon(eyeOff)
            setType('password')
        }
    }

    const signupSchema = Yup.object({
            email: Yup.string().email('Invalid Email Address').required('Email Required'),
            username: Yup.string()
                .min(3, "Username must be between 3 and 15 characters")
                .max(15, "Username must be between 3 and 15 characters")
                .required('Username Required'),
            firstName: Yup.string()
                .min(2, "First Name must be at least 2 characters")
                .required('First Name Required'),
            lastName: Yup.string()
                .min(2, "Last name must be at least 2 characters")
                .required('Last name Required'),
        }
    )
    const passwordValidationSchema = Yup.object().shape({
        password: Yup.string()
            .min(8, 'Password must be at least 8 characters')
            .max(20, 'Password must not exceed 20 characters')
            .matches(
            /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)[A-Za-z\d@$!%*?&]/,
            'Password must contain at least 1 uppercase letter, 1 lowercase letter, and 1 number')
            .required('Password is required'),
        confirmPassword: Yup.string()
            .oneOf([Yup.ref('password'), null], 'Passwords must match')
            .required('Must confirm your password'),
        });

    const combinedSchema = Yup.object().shape({
        ...signupSchema.fields,
        ...passwordValidationSchema.fields,
    });

    const formik = useFormik({
        initialValues: {
            email: '',
            username: '',
            firstName: '',
            lastName: '',
            password: '',
            confirmPassword: ''
        },
        validationSchema: combinedSchema,
        onSubmit: (values, {resetForm}) => {
            fetch('/users', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(values)
            }).then((r) => {
                console.log(values)
                if (r.ok) {
                    resetForm({values: ''})
                    r.json().then(({user}) => {
                        setUser(user)
                        //navigate into site
                    })
                } else {
                    console.log('Error fetching user')
                }
            })
        }
    })

    return (
        <div className={styles.signup_container}>
        <body>
            <main className={`${styles.formSignin} ${styles.bgGradient}`}>
                <h1>Create an Account</h1>
                <h2>Find Your Next Escape</h2>
                <Form onSubmit={formik.handleSubmit}>
                <Container className=''>
                    <Row className='mb-7'>
                    <Col md={6}>
                        <Form.Group 
                        className={styles.formGroup} 
                        controlId="formFirstName"
                        >
                            <Form.Control
                                type="text"
                                placeholder="First name"
                                name='firstName'
                                className={`form-control input-placeholder-custom ${styles.formControl}`}
                                id='form-first-name'
                                value={formik.values.firstName}
                                onChange={formik.handleChange}
                                onBlur={formik.handleBlur}
                                tabIndex={1}
                                style={{
                                marginRight: '100px',
                                marginLeft: '-40px'
                            }}
                            />
                            {formik.touched.firstName && formik.errors.firstName && (
                                <div 
                                className="error"
                                style={{
                                    color: '#F0DAAE',
                                    fontFamily: 'Questrial, sans-serif',
                                    fontSize: '15px',
                                    marginLeft: '-30px',
                                    marginTop: '-12px',
                                }}>{formik.errors.firstName}</div>
                            )}
                        </Form.Group>

                        <Form.Group 
                        className={styles.formGroup} 
                        controlId="formUsername">
                        <Form.Control
                            type="text"
                            placeholder="Username"
                            name='username'
                            value={formik.values.username}
                            onChange={formik.handleChange}
                            onBlur={formik.handleBlur}
                            className={`form-control input-placeholder-custom ${styles.formControl}`}
                            tabIndex={3}
                            style={{
                                marginRight: '100px',
                                marginLeft: '-40px'
                            }}
                        />
                        {formik.touched.username && formik.errors.username && (
                            <div className="error"
                            style={{
                                color: '#F0DAAE',
                                fontFamily: 'Questrial, sans-serif',
                                fontSize: '16px',
                                marginLeft: '-30px',
                                marginTop: '-12px',
                            }}
                            >{formik.errors.username}</div>
                        )}
                        </Form.Group>

                        <Form.Group 
                        className={styles.formGroup} 
                        controlId="formPassword">
                        <Form.Control
                            type={type}
                            placeholder="Password"
                            name='password'
                            dataToggle="password"
                            className={`form-control input-placeholder-custom ${styles.formControl}`}
                            value={formik.values.password}
                            onChange={formik.handleChange}
                            onBlur={formik.handleBlur}
                            tabIndex={5}
                            style={{
                                marginRight: '100px',
                                marginLeft: '-40px'
                            }}
                        />
                        <span className="flex justify-around items-center" onClick={handleToggle}>
                            <Icon className="absolute mr-10" icon={icon} size={25}/>
                        </span>
                        {formik.touched.password && formik.errors.password && (
                            <div className="error"
                            style={{
                                color: '#F0DAAE',
                                fontFamily: 'Questrial, sans-serif',
                                fontSize: '15px',
                                marginLeft: '-30px',
                                marginTop: '-12px',
                                width: '140%'
                            }}
                            >{formik.errors.password}</div>
                        )}
                        </Form.Group>

                    </Col>

                    <Col md={6}>
                        <Form.Group 
                        className={styles.formGroup} 
                        controlId="formLastName">
                            <Form.Control
                                type="text"
                                placeholder="Last Name"
                                name='lastName'
                                className={`form-control input-placeholder-custom ${styles.formControl}`}
                                value={formik.values.lastName}
                                onChange={formik.handleChange}
                                onBlur={formik.handleBlur}
                                tabIndex={2}
                                style={{marginLeft: '10px'}}
                            />
